{"customStyles":".cpq-discounts-list_container{
    border-bottom: 1px solid #DDDBDA !important;
    border-radius: 0px;
    padding: 13px 18px 13px 40px;
}
.cpq-discounts_fields label{
    text-transform:capitalize ;
    font-size: 10px;
    color: rgba(0, 0, 0, 0.6);
}
.cpq-discounts_fields.uppercase label{
    text-transform: uppercase; 
}
.cpq-discounts_fields .field-value{
    font-size: 16px;
    color: #000000;
}
.approved__icon{
    text-align: center;
}
.notsubmit__icon{
  text-align: center;
}
