    background-color: #E8912D; 
    border-radius: 100px;
    margin-left: -20px;
    text-align: center;
}
.approved__icon .slds-icon-action-approval{
    background-color: #4aca81; 
    border-radius: 100px;
    margin-left: -20px;
    text-align: center;
}
svg.slds-icon.slds-icon_container.slds-icon-action-approval.slds-icon_container--circle {
    fill: #fff !important;
}
.cpq-discounts_dynamic-align{
    text-align: center;
}
.cpq_discount_applies_all{
    font-size: 10px;
    color: rgba(0, 0, 0, 0.6);
}
@media only screen and (max-width: 48em) {
  .approved__icon{
    text-align: left;
    margin-left: 30px;
  }
  .cpq-discounts_dynamic-align{
    text-align: left;
  }
  .cpq-discounts_fields .field-value {
    font-size: 12px;
  }
  .cpq-discounts_fields label{
